sql.where:
id != 1
and ( t1.username like concat('%', #{keyword}, '%') or t1.name like concat('%', #{keyword}, '%') or t1.phone like concat(#{keyword}, '%') or t1.email like concat(#{keyword}, '%') )
and t1.login_time >= str_to_date(#{beginTime}, '%Y-%m-%d')
and t1.login_time <= str_to_date(#{endTime}, '%Y-%m-%d')
and role_id = #{roleId}
and user_type = #{userType}

mapper.select:
  select 
  
	  from sys_user
	  where username = #{username,jdbcType=VARCHAR} and password = #{password, jdbcType=VARCHAR}
